using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Windows.Data;
using System.IO;
using System.Windows.Media.Imaging;
using System.Xml.Linq;
using System.ComponentModel;


namespace pfVisualisator {

    public class mypict {

        private Guid ida;
        private int height;
        private int width;
        private string fmt;
        private string comto;
        private byte[] valo;

        public mypict() { }

        /// <summary>
        /// Модификация от 9 января 2015 года
        /// Заложен 23 декабря 2014 года
        /// </summary>
        /// <param name="ph"></param>
        /// <param name="pw"></param>
        /// <param name="pf"></param>
        /// <param name="pcomment"></param>
        /// <param name="piso"></param>
        public mypict(int ph, int pw, string pf, string pcomment, string piso)
        {
            this.ida = Guid.NewGuid();
            this.height = ph;
            this.width = pw;
            this.fmt = pf;
            this.comto = pcomment;
            this.valo = Convert.FromBase64String(piso);
        }

        /// <summary>
        /// Модификация от 25 февраля 2015 года
        /// Заложен 25 февраля 2015 года
        /// </summary>
        /// <param name="pid"></param>
        /// <param name="ph"></param>
        /// <param name="pw"></param>
        /// <param name="pf"></param>
        /// <param name="pcomment"></param>
        /// <param name="pmassiv"></param>
        private mypict(Guid pid, int ph, int pw, string pf, string pcomment, byte[] pmassiv) {
            this.ida = pid;
            this.height = ph;
            this.width = pw;
            this.fmt = pf;
            this.comto = pcomment;
            this.valo = pmassiv;
            }

        /// <summary>
        /// Модификация от 9 января 2015 года
        /// Заложен от 8 января 2015 года
        /// </summary>
        /// <param name="Elemo"></param>
        public mypict(XElement Elemo) {
            this.ida = Guid.Parse(Elemo.Attribute("Pid").Value);
            this.height = int.Parse(Elemo.Attribute("Heighto").Value);
            this.width = int.Parse(Elemo.Attribute("Widtho").Value);
            this.fmt = Elemo.Attribute("Fmt").Value;
            this.comto = ( Elemo.Attribute("Comto") != null ) ? Elemo.Attribute("Comto").Value : string.Empty;
            this.valo = Convert.FromBase64String(Elemo.Value);
            }

        /// <summary>
        /// Модификация от 15 января 2015 года
        /// Заложен от 15 января 2015 года
        /// </summary>
        /// <param name="ima"></param>
        public mypict(byte[] ima) {
            this.ida = Guid.NewGuid();
            this.comto = "Ещё не определялись";
            MemoryStream stream = new MemoryStream(ima);
            BitmapImage image = new BitmapImage();
            image.BeginInit();
            image.StreamSource = stream;
            image.EndInit();
            this.height = image.PixelHeight;
            this.width = image.PixelWidth;
            this.fmt = image.Format.ToString();
            this.valo = ima;
            }

        /// <summary>
        /// Модификация от 22 января 2015 года
        /// Заложен 21 января 2015 года
        /// </summary>
        /// <param name="imso"></param>
        public mypict(BitmapSource imso) {
            this.ida = Guid.NewGuid();
            this.comto = "Взяли из буфера";
            this.height = imso.PixelHeight;
            this.width = imso.PixelWidth;
            this.fmt = imso.Format.ToString();
//            PngBitmapEncoder bimapo = new PngBitmapEncoder(); //Удивительно, но так не работает
            JpegBitmapEncoder bimapo = new JpegBitmapEncoder(); //А так работает!!!
            bimapo.Frames.Add(BitmapFrame.Create(imso));
            using(MemoryStream ms = new MemoryStream()) {
                bimapo.Save(ms);
                this.valo = ms.ToArray();
                }
            }

        /// <summary>
        /// Модификация от 9 января 2015 года
        /// Заложен 23 декабря 2014 года
        /// </summary>
        /// <returns></returns>
        public XElement LeoXML() {
            XElement xRet = new XElement("Picta", new XAttribute("Pid", ida), new XAttribute("Fmt", fmt), new XAttribute("Heighto", height),
                                          new XAttribute("Widtho", width), new XAttribute("Comto", comto), Convert.ToBase64String(valo));
            return xRet;
            }

        /// <summary>
        /// Возвращает клон текущего объекта.
        /// Сама байтная картинка не клонируется, это тот же массив байтов
        /// Равенство будем отслеживать по гвиду, на мелочные факторы (размеры, описание) внимания не обращаем.
        /// При замене байтного массива будет другой гвид.
        /// Модификация от 25 февраля 2015 года
        /// Заложен 25 февраля 2015 года
        /// </summary>
        /// <returns>Клон текущего объекта</returns>
        public mypict Clone() {
            mypict reto = new mypict(ida, height, width, fmt, comto, valo);
            return reto;
            }

        /// <summary>
        /// Модификация от 15 января 2015 года
        /// Заложен от 15 января 2015 года
        /// </summary>
        /// <returns></returns>
        public static mypict ZagoPusta()
        {
            mypict reto = null;
            int kh = 802;
            int kw = 1810;
            string kf = "Png";
            string kcomto = "Описание картинки";
            string skartinka = "";
            reto = new mypict(kh, kw, kf, kcomto, skartinka);
            return reto;
        }

        public byte[] Picture { get { return valo; } }
        public string Commento { get { return comto; } set { comto = value; } }
        public Guid Gvido { get { return ida; } }
    }

    /// <summary>
    /// Модификация от 16 марта 2015 года
    /// Заложен 16 марта 2015 года
    /// </summary>
    public class PictoSeto : INotifyPropertyChanged {
        private List<mypict> listo;
        private int pictureindex;
        public event PropertyChangedEventHandler PropertyChanged;

        private PictoSeto() {
            listo = null;
            pictureindex = 0;
            }

        public PictoSeto(List<mypict> aa) {
            listo = aa;
            pictureindex = 0;
            }

        public void PositionChange(int sdvigo) {
            int newpos = pictureindex + sdvigo;
            if (newpos < 0 || newpos >= listo.Count) { return; }
            pictureindex = newpos;
            OnPropertyChanged("CurPicture");
            OnPropertyChanged("PicCurrent");
            OnPropertyChanged("CurPictDescription");
            OnPropertyChanged("CurPictPositionIz");
        }

        private void OnPropertyChanged(string name) {
            PropertyChangedEventHandler handler = PropertyChanged;
            if (handler != null) {
                handler(this, new PropertyChangedEventArgs(name));
                }
            }

        public mypict SamoPicture { get { return listo.Count == 0 ? null : listo[pictureindex]; } }
        public byte[] CurPicture { get { return listo.Count == 0 ? null : listo[pictureindex].Picture; } }
        public string CurPictDescription { get { return listo.Count == 0 ? "Нет картинки" : listo[pictureindex].Commento; } }
        public int PicCount { get { return listo.Count; } }
        public string CurPictPositionIz { get { return string.Format("{0} из {1}", PicCurrent, PicCount); } }
        public int PicCurrent { get { return (listo.Count > 0) ? pictureindex + 1 : 0; } }
        }

    public class BinaryImageConverter : IValueConverter
    {
        object IValueConverter.Convert(object value,
            Type targetType,
            object parameter,
            System.Globalization.CultureInfo culture)
        {
            if (value != null && value is byte[])
            {
                byte[] bytes = value as byte[];

                MemoryStream stream = new MemoryStream(bytes);

                BitmapImage image = new BitmapImage();
                image.BeginInit();
                image.StreamSource = stream;
                image.EndInit();

                return image;
            }

            return null;
        }

        object IValueConverter.ConvertBack(object value,
            Type targetType,
            object parameter,
            System.Globalization.CultureInfo culture)
        {
            throw new Exception("The method or operation is not implemented. Не предполагали :(");
        }
    }
}
